mapper.select:
SELECT usr.CD_RA AS ra, usr.NR_CPF AS cpf, usr.DS_METODO_ACESSO AS metodoAcesso, usr.DS_SENHA AS senha, usr.id_pessoa, usr.CD_SISTEMA_ORIGEM AS sistema, cur.SQ_CURSO, cur.CD_SISTEMA_ORIGEM, cur.NM_CURSO, alu.CD_SITUACAO_FINANCEIRA AS statusFinanceiro, alu.DS_PERIODO_SIT_ACADEMICA AS periodoLetivo, alu.CD_SITUACAO_ACADEMICA AS statusAcademico FROM Nucleo_academico.na_tb_usuario usr JOIN Nucleo_academico.na_tb_aluno alu ON alu.id_pessoa = usr.id_pessoa JOIN Nucleo_academico.na_tb_pessoa pes ON pes.id_pessoa = alu.id_pessoa JOIN Nucleo_academico.na_tb_curso cur ON cur.SQ_CURSO = alu.sq_curso
AND usr.NR_CPF = #{param1, jdbcType=VARCHAR}
AND usr.DS_EMAIL_OFFICE365 = #{param2, jdbcType=VARCHAR}
